package io.goodforgod.dummymaker.generator.simple.string;

import static java.util.regex.Pattern.CASE_INSENSITIVE;

import io.goodforgod.dummymaker.generator.Generator;
import java.util.regex.Pattern;

/**
 * Generates photo as base64
 *
 * @author Anton Kurako (GoodforGod)
 * @since 04.12.2021
 */
public final class PhotoGenerator implements Generator<CharSequence> {

    private static final Pattern PATTERN = Pattern.compile("pic(ture)?|photo|avatar|base64", CASE_INSENSITIVE);

    private static final String PHOTO_JPG_AS_BASE64 = "";

    @Override
    public String get() {
        return PHOTO_JPG_AS_BASE64;
    }

    @Override
    public Pattern pattern() {
        return PATTERN;
    }
}
